import React from "react";
import { motion } from "framer-motion";
import CategoryCard from "../Common/CategoryCard";
import categories from "../../data/categories";

const FeaturedCategories = () => {
  return (
    <section className="py-16 bg-white">
      <div className="container mx-auto px-4">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.5 }}
          viewport={{ once: true, margin: "-100px" }}
          className="text-center mb-12"
        >
          <h2 className="text-3xl font-bold text-primary mb-4">Shop by Category</h2>
          <p className="text-gray-600 max-w-xl mx-auto">
            Explore our curated collections for men, women, and kids, featuring the latest trends and timeless essentials.
          </p>
        </motion.div>

        {/* Category Grid - Desktop */}
        <div className="hidden md:grid grid-cols-1 md:grid-cols-3 gap-6">
          {categories.map((category, index) => (
            <motion.div
              key={category.id}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              viewport={{ once: true, margin: "-100px" }}
            >
              <CategoryCard category={category} variant={index === 1 ? "overlay" : "default"} />
            </motion.div>
          ))}
        </div>

        {/* Category Grid - Mobile (Simpler layout) */}
        <div className="grid grid-cols-1 gap-6 md:hidden">
          {categories.map((category, index) => (
            <motion.div
              key={category.id}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              viewport={{ once: true, margin: "-100px" }}
            >
              <CategoryCard category={category} variant="horizontal" />
            </motion.div>
          ))}
        </div>

        {/* View All Categories Button */}
        <div className="text-center mt-10">
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            className="inline-block border-2 border-primary text-primary hover:bg-primary hover:text-white font-medium px-8 py-3 rounded-md transition-colors"
          >
            View All Categories
          </motion.button>
        </div>
      </div>
    </section>
  );
};

export default FeaturedCategories;
