import React, { createContext, useContext, useEffect, useState } from 'react';

// Create admin data context
const AdminDataContext = createContext();

// Custom hook to use admin data context
export const useAdminData = () => {
  return useContext(AdminDataContext);
};

export const AdminDataProvider = ({ children }) => {
  // State for all data
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [orders, setOrders] = useState([]);
  const [users, setUsers] = useState([]);
  const [heroContent, setHeroContent] = useState({});
  const [promoBanner, setPromoBanner] = useState({});
  const [testimonials, setTestimonials] = useState([]);
  const [loading, setLoading] = useState(true);

  // Load data from localStorage on mount
  useEffect(() => {
    const loadData = async () => {
      try {
        // Load products
        const savedProducts = localStorage.getItem('adminProducts');
        if (savedProducts) {
          setProducts(JSON.parse(savedProducts));
        } else {
          // Initialize with sample data if none exists
          const sampleProducts = getSampleProducts();
          setProducts(sampleProducts);
          localStorage.setItem('adminProducts', JSON.stringify(sampleProducts));
        }

        // Load categories
        const savedCategories = localStorage.getItem('adminCategories');
        if (savedCategories) {
          setCategories(JSON.parse(savedCategories));
        } else {
          try {
            // Use categories from existing data file
            const categoriesModule = await import('../../data/categories');
            const categoriesData = categoriesModule.default;
            setCategories(categoriesData);
            localStorage.setItem('adminCategories', JSON.stringify(categoriesData));
          } catch (error) {
            console.error("Error loading categories:", error);
            // Fallback to empty categories
            setCategories([]);
          }
        }

        // Load orders
        const savedOrders = localStorage.getItem('orders');
        if (savedOrders) {
          setOrders(JSON.parse(savedOrders));
        } else {
          const sampleOrders = getSampleOrders();
          setOrders(sampleOrders);
          localStorage.setItem('orders', JSON.stringify(sampleOrders));
        }

        // Load users
        const savedUsers = localStorage.getItem('adminUsers');
        if (savedUsers) {
          setUsers(JSON.parse(savedUsers));
        } else {
          const sampleUsers = getSampleUsers();
          setUsers(sampleUsers);
          localStorage.setItem('adminUsers', JSON.stringify(sampleUsers));
        }

        // Load hero content
        const savedHeroContent = localStorage.getItem('adminHeroContent');
        if (savedHeroContent) {
          setHeroContent(JSON.parse(savedHeroContent));
        } else {
          // Use default hero content
          const defaultHeroContent = {
            title: "Summer Collection 2024",
            heading: "Discover Your Perfect Style",
            description: "Explore our curated collection of contemporary fashion for men, women, and kids. Quality designs that blend comfort and style for every occasion.",
            image: "/assets/hero-main.jpg",
            link: "/shop",
            color: "bg-accent"
          };
          setHeroContent(defaultHeroContent);
          localStorage.setItem('adminHeroContent', JSON.stringify(defaultHeroContent));
        }

        // Load promo banner
        const savedPromoBanner = localStorage.getItem('adminPromoBanner');
        if (savedPromoBanner) {
          setPromoBanner(JSON.parse(savedPromoBanner));
        } else {
          // Use default promo banner
          const defaultPromoBanner = {
            title: "Summer Sale",
            subtitle: "Up to 50% off on selected items",
            endDate: new Date(Date.now() + 7 * 24 * 60 * 60 * 1000).toISOString(),
            image: "/assets/promo-banner.jpg",
            buttonText: "Shop Now",
            buttonLink: "/shop"
          };
          setPromoBanner(defaultPromoBanner);
          localStorage.setItem('adminPromoBanner', JSON.stringify(defaultPromoBanner));
        }

        // Load testimonials
        const savedTestimonials = localStorage.getItem('adminTestimonials');
        if (savedTestimonials) {
          setTestimonials(JSON.parse(savedTestimonials));
        } else {
          const sampleTestimonials = getSampleTestimonials();
          setTestimonials(sampleTestimonials);
          localStorage.setItem('adminTestimonials', JSON.stringify(sampleTestimonials));
        }

        setLoading(false);
      } catch (error) {
        console.error("Error loading data:", error);
        setLoading(false);
      }
    };

    loadData();
  }, []);

  // Save data to localStorage whenever it changes
  useEffect(() => {
    if (!loading) {
      localStorage.setItem('adminProducts', JSON.stringify(products));
      localStorage.setItem('adminCategories', JSON.stringify(categories));
      localStorage.setItem('orders', JSON.stringify(orders));
      localStorage.setItem('adminUsers', JSON.stringify(users));
      localStorage.setItem('adminHeroContent', JSON.stringify(heroContent));
      localStorage.setItem('adminPromoBanner', JSON.stringify(promoBanner));
      localStorage.setItem('adminTestimonials', JSON.stringify(testimonials));
    }
  }, [products, categories, orders, users, heroContent, promoBanner, testimonials, loading]);

  // CRUD operations for products
  const addProduct = (product) => {
    const newProduct = {
      ...product,
      id: Date.now().toString(),
      createdAt: new Date().toISOString()
    };
    setProducts([...products, newProduct]);
    return newProduct;
  };

  const updateProduct = (id, updatedProduct) => {
    const updatedProducts = products.map(product => 
      product.id === id ? { ...product, ...updatedProduct, updatedAt: new Date().toISOString() } : product
    );
    setProducts(updatedProducts);
  };

  const deleteProduct = (id) => {
    setProducts(products.filter(product => product.id !== id));
  };

  // CRUD operations for categories
  const addCategory = (category) => {
    const newCategory = {
      ...category,
      id: category.id || Date.now().toString(),
      subcategories: category.subcategories || []
    };
    setCategories([...categories, newCategory]);
    return newCategory;
  };

  const updateCategory = (id, updatedCategory) => {
    const updatedCategories = categories.map(category => 
      category.id === id ? { ...category, ...updatedCategory } : category
    );
    setCategories(updatedCategories);
  };

  const deleteCategory = (id) => {
    setCategories(categories.filter(category => category.id !== id));
  };

  // CRUD operations for subcategories
  const addSubcategory = (categoryId, subcategory) => {
    const updatedCategories = categories.map(category => {
      if (category.id === categoryId) {
        return {
          ...category,
          subcategories: [...category.subcategories, {
            ...subcategory,
            id: subcategory.id || Date.now().toString()
          }]
        };
      }
      return category;
    });
    setCategories(updatedCategories);
  };

  const updateSubcategory = (categoryId, subcategoryId, updatedSubcategory) => {
    const updatedCategories = categories.map(category => {
      if (category.id === categoryId) {
        return {
          ...category,
          subcategories: category.subcategories.map(subcategory => 
            subcategory.id === subcategoryId ? { ...subcategory, ...updatedSubcategory } : subcategory
          )
        };
      }
      return category;
    });
    setCategories(updatedCategories);
  };

  const deleteSubcategory = (categoryId, subcategoryId) => {
    const updatedCategories = categories.map(category => {
      if (category.id === categoryId) {
        return {
          ...category,
          subcategories: category.subcategories.filter(subcategory => subcategory.id !== subcategoryId)
        };
      }
      return category;
    });
    setCategories(updatedCategories);
  };

  // CRUD operations for orders
  const updateOrder = (id, updatedOrder) => {
    const updatedOrders = orders.map(order => 
      order.id === id ? { ...order, ...updatedOrder } : order
    );
    setOrders(updatedOrders);
  };

  // CRUD operations for users
  const addUser = (user) => {
    const newUser = {
      ...user,
      id: Date.now().toString(),
      createdAt: new Date().toISOString()
    };
    setUsers([...users, newUser]);
    return newUser;
  };

  const updateUser = (id, updatedUser) => {
    const updatedUsers = users.map(user => 
      user.id === id ? { ...user, ...updatedUser } : user
    );
    setUsers(updatedUsers);
  };

  const deleteUser = (id) => {
    setUsers(users.filter(user => user.id !== id));
  };

  // Update homepage content
  const updateHeroContent = (content) => {
    setHeroContent({ ...heroContent, ...content });
  };

  const updatePromoBanner = (content) => {
    setPromoBanner({ ...promoBanner, ...content });
  };

  // CRUD operations for testimonials
  const addTestimonial = (testimonial) => {
    const newTestimonial = {
      ...testimonial,
      id: Date.now().toString(),
      createdAt: new Date().toISOString()
    };
    setTestimonials([...testimonials, newTestimonial]);
    return newTestimonial;
  };

  const updateTestimonial = (id, updatedTestimonial) => {
    const updatedTestimonials = testimonials.map(testimonial => 
      testimonial.id === id ? { ...testimonial, ...updatedTestimonial } : testimonial
    );
