import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAdminData } from '../context/AdminDataContext';
import ImageUploader from './common/ImageUploader';

const CategoryForm = ({ category = null, isEdit = false }) => {
  const navigate = useNavigate();
  const { addCategory, updateCategory } = useAdminData();
  
  // Form state
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    image: null,
    featured: false,
    subcategories: []
  });
  
  const [subcategories, setSubcategories] = useState([]);
  const [newSubcategory, setNewSubcategory] = useState('');
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  
  // Initialize form with category data if editing
  useEffect(() => {
    if (isEdit && category) {
      setFormData({
        name: category.name || '',
        description: category.description || '',
        image: category.image ? [{ path: category.image }] : [],
        featured: category.featured || false
      });
      
      setSubcategories(category.subcategories || []);
    }
  }, [isEdit, category]);
  
  // Handle form input changes
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    
    setFormData({
      ...formData,
      [name]: type === 'checkbox' ? checked : value
    });
    
    // Clear error when field is being edited
    if (errors[name]) {
      setErrors({ ...errors, [name]: '' });
    }
  };
  
  // Handle image changes
  const handleImageChange = (images) => {
    setFormData({ ...formData, image: images[0] || null });
    
    // Clear error when field is being edited
    if (errors.image) {
      setErrors({ ...errors, image: '' });
    }
  };
  
  // Add new subcategory
  const handleAddSubcategory = () => {
    if (!newSubcategory.trim()) return;
    
    // Check if subcategory with same name already exists
    if (subcategories.some(sub => sub.name.toLowerCase() === newSubcategory.trim().toLowerCase())) {
      setErrors({ ...errors, subcategory: 'A subcategory with this name already exists' });
      return;
    }
    
    // Add new subcategory
    const newSub = {
      id: `sub_${Date.now()}`,
      name: newSubcategory.trim()
    };
    
    setSubcategories([...subcategories, newSub]);
    setNewSubcategory('');
    
    // Clear error if it exists
    if (errors.subcategory) {
      setErrors({ ...errors, subcategory: '' });
    }
    
    // Clear error if it exists
    if (errors.subcategories) {
      setErrors({ ...errors, subcategories: '' });
    }
  };
  
  // Remove subcategory
  const handleRemoveSubcategory = (id) => {
    setSubcategories(subcategories.filter(sub => sub.id !== id));
  };
  
  // Validate form
  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.name.trim()) {
      newErrors.name = 'Category name is required';
    }
    
    if (!formData.description.trim()) {
      newErrors.description = 'Category description is required';
    }
    
    if (!formData.image) {
      newErrors.image = 'Category image is required';
    }
    
    if (subcategories.length === 0) {
      newErrors.subcategories = 'At least one subcategory is required';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };
  
  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    
    if (validateForm()) {
      // Prepare category data
      const categoryData = {
        ...formData,
        image: formData.image?.path || '',
        subcategories
      };
      
      try {
        if (isEdit && category) {
          // Update existing category
          updateCategory(category.id, categoryData);
        } else {
          // Add new category
          addCategory(categoryData);
        }
        
        // Navigate back to categories list
        navigate('/admin/categories');
      } catch (error) {
        console.error('Error saving category:', error);
        setErrors({ submit: 'Failed to save category. Please try again.' });
      } finally {
        setIsSubmitting(false);
      }
    } else {
      setIsSubmitting(false);
    }
  };
  
  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      {errors.submit && (
        <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded">
          {errors.submit}
        </div>
      )}
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Basic Information */}
        <div className="bg-white p-6 rounded-lg shadow-sm">
          <h2 className="text-lg font-medium text-primary mb-4">Category Information</h2>
          
          <div className="space-y-4">
            <div>
              <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-1">
                Category Name <span className="text-red-500">*</span>
              </label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                className={`w-full px-3 py-2 border rounded-md ${
                  errors.name ? 'border-red-500' : 'border-gray-300'
                } focus:outline-none focus:ring-1 focus:ring-accent focus:border-accent`}
              />
              {errors.name && <p className="mt-1 text-sm text-red-500">{errors.name}</p>}
            </div>
            
            <div>
              <label htmlFor="description" className="block text-sm font-medium text-gray-700 mb-1">
                Description <span className="text-red-500">*</span>
              </label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleChange}
                rows="4"
                className={`w-full px-3 py-2 border rounded-md ${
                  errors.description ? 'border-red-500' : 'border-gray-300'
                } focus:outline-none focus:ring-1 focus:ring-accent focus:border-accent`}
              ></textarea>
              {errors.description && <p className="mt-1 text-sm text-red-500">{errors.description}</p>}
            </div>
            
            <div className="flex items-center">
              <input
                type="checkbox"
                id="featured"
                name="featured"
                checked={formData.featured}
                onChange={handleChange}
                className="h-4 w-4 text-accent focus:ring-accent border-gray-300 rounded"
              />
              <label htmlFor="featured" className="ml-2 block text-sm text-gray-700">
                Featured Category
              </label>
            </div>
          </div>
        </div>
        
        {/* Category Image */}
        <div className="bg-white p-6 rounded-lg shadow-sm">
          <h2 className="text-lg font-medium text-primary mb-4">Category Image</h2>
          
          <ImageUploader
            images={formData.image ? [formData.image] : []}
            onChange={(images) => handleImageChange(images)}
            multiple={false}
            maxFiles={1}
          />
          
          {errors.image && <p className="mt-2 text-sm text-red-500">{errors.image}</p>}
        </div>
      </div>
      
      {/* Subcategories */}
      <div className="bg-white p-6 rounded-lg shadow-sm">
        <h2 className="text-lg font-medium text-primary mb-4">Subcategories</h2>
        
        <div className="space-y-4">
          <div className="flex items-end space-x-2">
            <div className="flex-1">
              <label htmlFor="newSubcategory" className="block text-sm font-medium text-gray-700 mb-1">
                Add Subcategory
              </label>
              <input
                type="text"
                id="newSubcategory"
                value={newSubcategory}
                onChange={(e) => setNewSubcategory(e.target.value)}
                className={`w-full px-3 py-2 border rounded-md ${
                  errors.subcategory ? 'border-red-500' : 'border-gray-300'
                } focus:outline-none focus:ring-1 focus:ring-accent focus:border-accent`}
                placeholder="Enter subcategory name"
              />
              {errors.subcategory && <p className="mt-1 text-sm text-red-500">{errors.subcategory}</p>}
            </div>
            
            <button
              type="button"
              onClick={handleAddSubcategory}
              className="px-4 py-2 bg-accent text-white rounded-md hover:bg-accent/90 transition-colors"
            >
              Add
            </button>
          </div>
          
          {errors.subcategories && <p className="text-sm text-red-500">{errors.subcategories}</p>}
          
          {subcategories.length > 0 ? (
            <ul className="mt-4 divide-y divide-gray-200 border border-gray-200 rounded-md overflow-hidden">
              {subcategories.map((subcategory) => (
                <li key={subcategory.id} className="flex justify-between items-center p-3 bg-gray-50">
                  <span className="font-medium text-gray-700">{subcategory.name}</span>
                  <button
                    type="button"
                    onClick={() => handleRemoveSubcategory(subcategory.id)}
                    className="text-red-500 hover:text-red-700"
                  >
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      className="h-5 w-5"
                      viewBox="0 0 20 20"
                      fill="currentColor"
                    >
                      <path
                        fillRule="evenodd"
                        d="M9 2a1 1 0 00-.894.553L7.382 4H4a1 1 0 000 2v10a2 2 0 002 2h8a2 2 0 002-2V6a1 1 0 100-2h-3.382l-.724-1.447A1 1 0 0011 2H9zM7 8a1 1 0 012 0v6a1 1 0 11-2 0V8zm5-1a1 1 0 00-1 1v6a1 1 0 102 0V8a1 1 0 00-1-1z"
                        clipRule="evenodd"
                      />
                    </svg>
                  </button>
                </li>
              ))}
            </ul>
          ) : (
            <p className="text-gray-500 italic">No subcategories added yet.</p>
          )}
        </div>
      </div>
      
      {/* Form Actions */}
      <div className="flex justify-end space-x-4">
        <button
          type="button"
          onClick={() => navigate('/admin/categories')}
          className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50 transition-colors"
        >
          Cancel
        </button>
        
        <button
          type="submit"
          disabled={isSubmitting}
          className="px-4 py-2 bg-accent text-white rounded-md hover:bg-accent/90 transition-colors disabled:bg-gray-400"
        >
          {isSubmitting ? 'Saving...' : isEdit ? 'Update Category' : 'Add Category'}
        </button>
      </div>
    </form>
  );
};

export default CategoryForm;
