import React from 'react';
import { FaBox, FaShoppingBag, FaUsers, FaDollarSign } from 'react-icons/fa';
import { StatCard, OrderStatusChart, RecentOrdersTable } from '../components/DashboardWidgets';
import { useAdminData } from '../context/AdminDataContext';

const Dashboard = () => {
  const { orders, getStats } = useAdminData();
  const stats = getStats();

  return (
    <div>
      <h1 className="text-2xl font-bold text-primary mb-6">Dashboard</h1>
      
      {/* Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        <StatCard 
          title="Total Products" 
          value={stats.totalProducts} 
          icon={FaBox} 
          change="12" 
          changeType="increase" 
        />
        <StatCard 
          title="Total Orders" 
          value={stats.totalOrders} 
          icon={FaShoppingBag} 
          change="5" 
          changeType="increase" 
        />
        <StatCard 
          title="Total Users" 
          value={stats.totalUsers} 
          icon={FaUsers} 
          change="8" 
          changeType="increase" 
        />
        <StatCard 
          title="Total Revenue" 
          value={`$${stats.totalRevenue.toFixed(2)}`} 
          icon={FaDollarSign} 
          change="15" 
          changeType="increase" 
        />
      </div>
      
      {/* Charts and Tables */}
      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Order Status Chart */}
        <div className="lg:col-span-1">
          <OrderStatusChart ordersByStatus={stats.ordersByStatus} />
        </div>
        
        {/* Recent Orders Table */}
        <div className="lg:col-span-2">
          <RecentOrdersTable orders={orders} />
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
