import React from "react";
import { Link } from "react-router-dom";
import { FaTrash } from "react-icons/fa";
import { formatCurrency } from "../../utils/formatters";
import { useCart } from "../../context/CartContext";

const CartItem = ({ item }) => {
  const { updateQuantity, removeItem } = useCart();

  const handleQuantityChange = (e) => {
    const newQuantity = parseInt(e.target.value);
    if (newQuantity > 0) {
      updateQuantity(item, newQuantity);
    }
  };

  const handleRemove = () => {
    removeItem(item);
  };

  return (
    <div className="flex flex-col sm:flex-row items-center py-4 border-b border-gray-200">
      {/* Product Image */}
      <div className="w-24 h-24 flex-shrink-0 mr-4 mb-4 sm:mb-0">
        <Link to={`/product/${item.id}`}>
          <img
            src={item.image}
            alt={item.name}
            className="w-full h-full object-cover rounded-md"
          />
        </Link>
      </div>

      {/* Product Info */}
      <div className="flex-grow">
        <Link to={`/product/${item.id}`} className="hover:text-accent transition-colors">
          <h3 className="font-medium text-primary">{item.name}</h3>
        </Link>
        <div className="text-sm text-gray-500 mt-1">
          {item.color && <span className="mr-2">Color: {item.color}</span>}
          {item.size && <span>Size: {item.size}</span>}
        </div>
        <div className="font-medium text-primary mt-1">{formatCurrency(item.price)}</div>
      </div>

      {/* Quantity Controls */}
      <div className="flex items-center mt-4 sm:mt-0 sm:ml-4">
        <div className="flex items-center border border-gray-300 rounded-md">
          <button
            onClick={() => updateQuantity(item, Math.max(1, item.quantity - 1))}
            className="w-8 h-8 flex items-center justify-center hover:bg-gray-100"
          >
            -
          </button>
          <input
            type="number"
            min="1"
            value={item.quantity}
            onChange={handleQuantityChange}
            className="w-10 h-8 text-center border-x border-gray-300 focus:outline-none"
          />
          <button
            onClick={() => updateQuantity(item, item.quantity + 1)}
            className="w-8 h-8 flex items-center justify-center hover:bg-gray-100"
          >
            +
          </button>
        </div>
      </div>

      {/* Subtotal */}
      <div className="font-medium text-primary ml-6 hidden sm:block">
        {formatCurrency(item.price * item.quantity)}
      </div>

      {/* Remove Button */}
      <button
        onClick={handleRemove}
        className="text-gray-400 hover:text-red-500 transition-colors ml-4"
        aria-label="Remove item"
      >
        <FaTrash />
      </button>
    </div>
  );
};

export default CartItem;
