import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAdminData } from '../context/AdminDataContext';
import ImageUploader from './common/ImageUploader';

const ProductForm = ({ product = null, isEdit = false }) => {
  const navigate = useNavigate();
  const { categories, addProduct, updateProduct } = useAdminData();
  
  // Form state
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    price: '',
    category: '',
    subCategory: '',
    gender: '',
    sizes: [],
    colors: [],
    stock: '',
    images: [],
    featured: false,
    trending: false
  });
  
  const [errors, setErrors] = useState({});
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState(null);
  
  // Available options
  const genderOptions = ['men', 'women', 'kids', 'unisex'];
  const sizeOptions = ['XS', 'S', 'M', 'L', 'XL', 'XXL', '4', '5', '6', '7', '8', '9', '10', '11', '12'];
  const colorOptions = [
    'Black', 'White', 'Red', 'Blue', 'Green', 'Yellow', 'Purple', 'Pink', 
    'Orange', 'Gray', 'Brown', 'Navy', 'Beige', 'Teal', 'Maroon'
  ];
  
  // Initialize form with product data if editing
  useEffect(() => {
    if (isEdit && product) {
      setFormData({
        name: product.name || '',
        description: product.description || '',
        price: product.price || '',
        category: product.category || '',
        subCategory: product.subCategory || '',
        gender: product.gender || '',
        sizes: product.sizes || [],
        colors: product.colors || [],
        stock: product.stock || '',
        images: product.images ? product.images.map(path => ({ path })) : [],
        featured: product.featured || false,
        trending: product.trending || false
      });
      
      // Set selected category
      if (product.category) {
        const category = categories.find(cat => cat.id === product.category);
        setSelectedCategory(category);
      }
    }
  }, [isEdit, product, categories]);
  
  // Update subcategories when category changes
  useEffect(() => {
    if (formData.category) {
      const category = categories.find(cat => cat.id === formData.category);
      setSelectedCategory(category);
      
      // Reset subcategory if it doesn't exist in the new category
      if (category && !category.subcategories.some(sub => sub.id === formData.subCategory)) {
        setFormData(prev => ({ ...prev, subCategory: '' }));
      }
    }
  }, [formData.category, categories]);
  
  // Handle form input changes
  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;
    
    if (type === 'checkbox') {
      setFormData({ ...formData, [name]: checked });
    } else {
      setFormData({ ...formData, [name]: value });
    }
    
    // Clear error when field is being edited
    if (errors[name]) {
      setErrors({ ...errors, [name]: '' });
    }
  };
  
  // Handle multi-select changes (sizes, colors)
  const handleMultiSelect = (e, field) => {
    const value = e.target.value;
    
    if (formData[field].includes(value)) {
      // Remove value if already selected
      setFormData({
        ...formData,
        [field]: formData[field].filter(item => item !== value)
      });
    } else {
      // Add value if not already selected
      setFormData({
        ...formData,
        [field]: [...formData[field], value]
      });
    }
    
    // Clear error when field is being edited
    if (errors[field]) {
      setErrors({ ...errors, [field]: '' });
    }
  };
  
  // Handle image changes
  const handleImageChange = (images) => {
    setFormData({ ...formData, images });
    
    // Clear error when field is being edited
    if (errors.images) {
      setErrors({ ...errors, images: '' });
    }
  };
  
  // Validate form
  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.name.trim()) {
      newErrors.name = 'Product name is required';
    }
    
    if (!formData.description.trim()) {
      newErrors.description = 'Product description is required';
    }
    
    if (!formData.price || isNaN(formData.price) || Number(formData.price) <= 0) {
      newErrors.price = 'Valid price is required';
    }
    
    if (!formData.category) {
      newErrors.category = 'Category is required';
    }
    
    if (!formData.subCategory) {
      newErrors.subCategory = 'Subcategory is required';
    }
    
    if (!formData.gender) {
      newErrors.gender = 'Gender is required';
    }
    
    if (formData.sizes.length === 0) {
      newErrors.sizes = 'At least one size is required';
    }
    
    if (formData.colors.length === 0) {
      newErrors.colors = 'At least one color is required';
    }
    
    if (!formData.stock || isNaN(formData.stock) || Number(formData.stock) < 0) {
      newErrors.stock = 'Valid stock quantity is required';
    }
    
    if (formData.images.length === 0) {
      newErrors.images = 'At least one image is required';
    }
    
    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };
  
  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    setIsSubmitting(true);
    
    if (validateForm()) {
      // Prepare product data
      const productData = {
        ...formData,
        price: Number(formData.price),
        stock: Number(formData.stock),
        images: formData.images.map(img => img.path)
      };
      
      try {
        if (isEdit && product) {
          // Update existing product
          updateProduct(product.id, productData);
        } else {
          // Add new product
          addProduct(productData);
        }
        
        // Navigate back to products list
        navigate('/admin/products');
      } catch (error) {
        console.error('Error saving product:', error);
        setErrors({ submit: 'Failed to save product. Please try again.' });
      } finally {
        setIsSubmitting(false);
      }
    } else {
      setIsSubmitting(false);
    }
  };
  
  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      {errors.submit && (
        <div className="bg-red-50 border border-red-200 text-red-700 px-4 py-3 rounded">
          {errors.submit}
        </div>
      )}
      
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        {/* Basic Information */}
        <div className="bg-white p-6 rounded-lg shadow-sm">
          <h2 className="text-lg font-medium text-primary mb-4">Basic Information</h2>
          
          <div className="space-y-4">
            <div>
              <label htmlFor="name" className="block text-sm font-medium text-gray-700 mb-1">
                Product Name <span className="text-red-500">*</span>
              </label>
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleChange}
                className={`w-full px-3 py-2 border rounded-md ${
                  errors.name ? 'border-red-500' : 'border-gray-300'
                } focus:outline-none focus:ring-1 focus:ring-accent focus:border-accent`}
              />
              {errors.name && <p className="mt-1 text-sm text-red-500">{errors.name}</p>}
            </div>
            
            <div>
              <label htmlFor="description" className="block text-sm font-medium text-gray-700 mb-1">
                Description <span className="text-red-500">*</span>
              </label>
              <textarea
                id="description"
                name="description"
                value={formData.description}
                onChange={handleChange}
                rows="4"
                className={`w-full px-3 py-2 border rounded-md ${
                  errors.description ? 'border-red-500' : 'border-gray-300'
                } focus:outline-none focus:ring-1 focus:ring-accent focus:border-accent`}
              ></textarea>
              {errors.description && <p className="mt-1 text-sm text-red-500">{errors.description}</p>}
            </div>
            
            <div>
              <label htmlFor="price" className="block text-sm font-medium text-gray-700 mb-1">
                Price ($) <span className="text-red-500">*</span>
              </label>
              <input
                type="number"
                id="price"
                name="price"
                value={formData.price}
                onChange={handleChange}
                min="0"
                step="0.01"
                className={`w-full px-3 py-2 border rounded-md ${
                  errors.price ? 'border-red-500' : 'border-gray-300'
                } focus:outline-none focus:ring-1 focus:ring-accent focus:border-accent`}
              />
              {errors.price && <p className="mt-1 text-sm text-red-500">{errors.price}</p>}
            </div>
            
            <div>
              <label htmlFor="stock" className="block text-sm font-medium text-gray-700 mb-1">
                Stock Quantity <span className="text-red-500">*</span>
              </label>
              <input
                type="number"
                id="stock"
                name="stock"
                value={formData.stock}
                onChange={handleChange}
                min="0"
                className={`w-full px-3 py-2 border rounded-md ${
                  errors.stock ? 'border-red-500' : 'border-gray-300'
                } focus:outline-none focus:ring-1 focus:ring-accent focus:border-accent`}
              />
              {errors.stock && <p className="mt-1 text-sm text-red-500">{errors.stock}</p>}
            </div>
            
            <div className="flex flex-wrap gap-4">
              <div className="flex items-center">
                <input
                  type="checkbox"
                  id="featured"
                  name="featured"
                  checked={formData.featured}
                  onChange={handleChange}
                  className="h-4 w-4 text-accent focus:ring-accent border-gray-300 rounded"
                />
                <label htmlFor="featured" className="ml-2 block text-sm text-gray-700">
                  Featured Product
                </label>
              </div>
              
              <div className="flex items-center">
                <input
                  type="checkbox"
                  id="trending"
                  name="trending"
                  checked={formData.trending}
                  onChange={handleChange}
                  className="h-4 w-4 text-accent focus:ring-accent border-gray-300 rounded"
                />
                <label htmlFor="trending" className="ml-2 block text-sm text-gray-700">
                  Trending Product
                </label>
              </div>
            </div>
          </div>
        </div>
        
        {/* Categorization */}
        <div className="bg-white p-6 rounded-lg shadow-sm">
          <h2 className="text-lg font-medium text-primary mb-4">Categorization</h2>
          
          <div className="space-y-4">
            <div>
              <label htmlFor="category" className="block text-sm font-medium text-gray-700 mb-1">
                Category <span className="text-red-500">*</span>
              </label>
              <select
                id="category"
                name="category"
                value={formData.category}
                onChange={handleChange}
                className={`w-full px-3 py-2 border rounded-md ${
                  errors.category ? 'border-red-500' : 'border-gray-300'
                } focus:outline-none focus:ring-1 focus:ring-accent focus:border-accent`}
              >
                <option value="">Select Category</option>
                {categories.map((category) => (
                  <option key={category.id} value={category.id}>
                    {category.name}
                  </option>
                ))}
              </select>
              {errors.category && <p className="mt-1 text-sm text-red-500">{errors.category}</p>}
            </div>
            
            <div>
              <label htmlFor="subCategory" className="block text-sm font-medium text-gray-700 mb-1">
                Subcategory <span className="text-red-500">*</span>
              </label>
              <select
                id="subCategory"
                name="subCategory"
                value={formData.subCategory}
                onChange={handleChange}
                disabled={!selectedCategory}
                className={`w-full px-3 py-2 border rounded-md ${
                  errors.subCategory ? 'border-red-500' : 'border-gray-300'
                } focus:outline-none focus:ring-1 focus:ring-accent focus:border-accent ${
                  !selectedCategory ? 'bg-gray-100' : ''
                }`}
              >
                <option value="">Select Subcategory</option>
                {selectedCategory?.subcategories.map((subcategory) => (
                  <option key={subcategory.id} value={subcategory.id}>
                    {subcategory.name}
                  </option>
                ))}
              </select>
              {errors.subCategory && <p className="mt-1 text-sm text-red-500">{errors.subCategory}</p>}
            </div>
            
            <div>
              <label htmlFor="gender" className="block text-sm font-medium text-gray-700 mb-1">
                Gender <span className="text-red-500">*</span>
              </label>
              <select
                id="gender"
                name="gender"
                value={formData.gender}
                onChange={handleChange}
                className={`w-full px-3 py-2 border rounded-md ${
                  errors.gender ? 'border-red-500' : 'border-gray-300'
                } focus:outline-none focus:ring-1 focus:ring-accent focus:border-accent`}
              >
                <option value="">Select Gender</option>
                {genderOptions.map((gender) => (
                  <option key={gender} value={gender}>
                    {gender.charAt(0).toUpperCase() + gender.slice(1)}
                  </option>
                ))}
              </select>
              {errors.gender && <p className="mt-1 text-sm text-red-500">{errors.gender}</p>}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Sizes <span className="text-red-500">*</span>
              </label>
              <div className="grid grid-cols-3 gap-2">
                {sizeOptions.map((size) => (
                  <div key={size} className="flex items-center">
                    <input
                      type="checkbox"
                      id={`size-${size}`}
                      value={size}
                      checked={formData.sizes.includes(size)}
                      onChange={(e) => handleMultiSelect(e, 'sizes')}
                      className="h-4 w-4 text-accent focus:ring-accent border-gray-300 rounded"
                    />
                    <label htmlFor={`size-${size}`} className="ml-2 block text-sm text-gray-700">
                      {size}
                    </label>
                  </div>
                ))}
              </div>
              {errors.sizes && <p className="mt-1 text-sm text-red-500">{errors.sizes}</p>}
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                Colors <span className="text-red-500">*</span>
              </label>
              <div className="grid grid-cols-3 gap-2">
                {colorOptions.map((color) => (
                  <div key={color} className="flex items-center">
                    <input
                      type="checkbox"
                      id={`color-${color}`}
                      value={color}
                      checked={formData.colors.includes(color)}
                      onChange={(e) => handleMultiSelect(e, 'colors')}
                      className="h-4 w-4 text-accent focus:ring-accent border-gray-300 rounded"
                    />
                    <label htmlFor={`color-${color}`} className="ml-2 block text-sm text-gray-700">
                      {color}
                    </label>
                  </div>
                ))}
              </div>
              {errors.colors && <p className="mt-1 text-sm text-red-500">{errors.colors}</p>}
            </div>
          </div>
        </div>
      </div>
      
      {/* Images */}
      <div className="bg-white p-6 rounded-lg shadow-sm">
        <h2 className="text-lg font-medium text-primary mb-4">Product Images</h2>
        
        <ImageUploader
          images={formData.images}
          onChange={handleImageChange}
          multiple={true}
          maxFiles={5}
        />
        
        {errors.images && <p className="mt-2 text-sm text-red-500">{errors.images}</p>}
      </div>
      
      {/* Form Actions */}
      <div className="flex justify-end space-x-4">
        <button
          type="button"
          onClick={() => navigate('/admin/products')}
          className="px-4 py-2 border border-gray-300 rounded-md text-gray-700 hover:bg-gray-50 transition-colors"
        >
          Cancel
        </button>
        
        <button
          type="submit"
          disabled={isSubmitting}
          className="px-4 py-2 bg-accent text-white rounded-md hover:bg-accent/90 transition-colors disabled:bg-gray-400"
        >
          {isSubmitting ? 'Saving...' : isEdit ? 'Update Product' : 'Add Product'}
        </button>
      </div>
    </form>
  );
};

export default ProductForm;
