import React from "react";
import Breadcrumb from "../components/Layout/Breadcrumb";

const Help = () => {
  // FAQ data
  const faqItems = [
    {
      question: "How do I track my order?",
      answer: "You can track your order by logging into your account and visiting the 'Order History' section. Alternatively, you can use the tracking number provided in your shipping confirmation email."
    },
    {
      question: "What is your return policy?",
      answer: "We accept returns within 30 days of purchase. Items must be unworn, unwashed, and with all original tags attached."
    },
    {
      question: "How long does shipping take?",
      answer: "Standard shipping typically takes 3-5 business days. Express shipping takes 1-2 business days."
    },
    {
      question: "Do you ship internationally?",
      answer: "Yes, we ship to most countries worldwide. International shipping costs and delivery times vary depending on the destination."
    }
  ];

  return (
    <div className="container mx-auto px-4 py-8">
      <Breadcrumb items={[
        { label: "Home", path: "/" },
        { label: "Help", path: "/help" }
      ]} />
      
      <div className="max-w-4xl mx-auto">
        <h1 className="text-3xl font-bold mb-4">Help Center</h1>
        <p className="text-lg text-gray-600 mb-8">
          Find answers to common questions and learn more about our policies.
        </p>
        
        <div className="space-y-6">
          <h2 className="text-2xl font-semibold mb-6">Frequently Asked Questions</h2>
          
          <div className="space-y-4">
            {faqItems.map((item, index) => (
              <div key={index} className="bg-white rounded-lg shadow-md p-6">
                <h3 className="text-lg font-medium mb-2">{item.question}</h3>
                <p className="text-gray-600">{item.answer}</p>
              </div>
            ))}
          </div>
          
          <div className="bg-indigo-50 rounded-lg p-6 mt-8">
            <h3 className="text-lg font-medium mb-2">Need more help?</h3>
            <p className="text-gray-600 mb-4">
              Contact our customer service team for assistance.
            </p>
            <div className="flex flex-col sm:flex-row gap-4">
              <a href="mailto:support@fashionstorename.com" className="bg-indigo-600 text-white py-2 px-4 rounded-md font-medium hover:bg-indigo-700 transition duration-300 text-center">
                Email Support
              </a>
              <a href="tel:+15551234567" className="bg-white text-indigo-600 border border-indigo-600 py-2 px-4 rounded-md font-medium hover:bg-gray-50 transition duration-300 text-center">
                Call Us
              </a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Help;
