import React, { useState, useEffect } from "react";
import { useSearchParams } from "react-router-dom";
import { FaSort, FaThLarge, FaThList, FaFilter } from "react-icons/fa";
import { motion } from "framer-motion";

import Breadcrumb from "../components/Layout/Breadcrumb";
import FilterSidebar from "../components/Common/FilterSidebar";
import ProductCard from "../components/Common/ProductCard";
import products from "../data/products";

const Shop = () => {
  const [searchParams, setSearchParams] = useSearchParams();
  const [displayedProducts, setDisplayedProducts] = useState([]);
  const [mobileFiltersOpen, setMobileFiltersOpen] = useState(false);
  const [gridView, setGridView] = useState(true);
  const [sortBy, setSortBy] = useState("newest");
  const [filters, setFilters] = useState({
    categories: [],
    subcategories: [],
    price: {
      min: "",
      max: "",
    },
    sizes: [],
    colors: [],
  });

  // Handle URL params on component mount
  useEffect(() => {
    const category = searchParams.get("category");
    const subcategory = searchParams.get("subcategory");
    const minPrice = searchParams.get("minPrice");
    const maxPrice = searchParams.get("maxPrice");
    const sizes = searchParams.getAll("size");
    const colors = searchParams.getAll("color");
    const sort = searchParams.get("sort");

    const initialFilters = {
      categories: category ? [category] : [],
      subcategories: subcategory ? [`${category}_${subcategory}`] : [],
      price: {
        min: minPrice || "",
        max: maxPrice || "",
      },
      sizes: sizes || [],
      colors: colors || [],
    };

    setFilters(initialFilters);
    if (sort) setSortBy(sort);
  }, [searchParams]);

  // Filter and sort products
  useEffect(() => {
    let filtered = [...products];

    // Filter by category
    if (filters.categories.length > 0) {
      filtered = filtered.filter((product) =>
        filters.categories.includes(product.category)
      );
    }

    // Filter by subcategory
    if (filters.subcategories.length > 0) {
      filtered = filtered.filter((product) => {
        return filters.subcategories.some((sub) => {
          const [category, subcategory] = sub.split("_");
          return (
            product.category === category && product.subCategory === subcategory
          );
        });
      });
    }

    // Filter by price
    if (filters.price.min !== "") {
      filtered = filtered.filter(
        (product) => product.price >= parseFloat(filters.price.min)
      );
    }
    if (filters.price.max !== "") {
      filtered = filtered.filter(
        (product) => product.price <= parseFloat(filters.price.max)
      );
    }

    // Filter by size
    if (filters.sizes.length > 0) {
      filtered = filtered.filter((product) =>
        product.sizes.some((size) => filters.sizes.includes(size))
      );
    }

    // Filter by color
    if (filters.colors.length > 0) {
      filtered = filtered.filter((product) =>
        product.colors.some((color) =>
          filters.colors.some((selectedColor) =>
            color.toLowerCase().includes(selectedColor.toLowerCase())
          )
        )
      );
    }

    // Sort products
    switch (sortBy) {
      case "price-low-high":
        filtered.sort((a, b) => a.price - b.price);
        break;
      case "price-high-low":
        filtered.sort((a, b) => b.price - a.price);
        break;
      case "rating":
        filtered.sort((a, b) => b.rating - a.rating);
        break;
      case "popularity":
        filtered.sort((a, b) => b.reviewCount - a.reviewCount);
        break;
      case "newest":
      default:
        // For this demo, we don't have date data, so just show in original order
        break;
    }

    setDisplayedProducts(filtered);
  }, [filters, sortBy]);

  // Update URL params when filters change
  useEffect(() => {
    const params = new URLSearchParams();

    // Add categories
    if (filters.categories.length === 1) {
      params.set("category", filters.categories[0]);
    }

    // Add subcategories
    filters.subcategories.forEach((sub) => {
      const [category, subcategory] = sub.split("_");
      if (filters.categories.includes(category)) {
        params.set("subcategory", subcategory);
      }
    });

    // Add price
    if (filters.price.min !== "") {
      params.set("minPrice", filters.price.min);
    }
    if (filters.price.max !== "") {
      params.set("maxPrice", filters.price.max);
    }

    // Add sizes
    filters.sizes.forEach((size) => {
      params.append("size", size);
    });

    // Add colors
    filters.colors.forEach((color) => {
      params.append("color", color);
    });

    // Add sort
    params.set("sort", sortBy);

    setSearchParams(params);
  }, [filters, sortBy, setSearchParams]);

  // Reset all filters
  const resetFilters = () => {
    setFilters({
      categories: [],
      subcategories: [],
      price: {
        min: "",
        max: "",
      },
      sizes: [],
      colors: [],
    });
  };

  // Handle sort change
  const handleSortChange = (e) => {
    setSortBy(e.target.value);
  };

  return (
    <div className="pt-20">
      <Breadcrumb />

      <div className="container mx-auto px-4 py-8">
        <div className="flex flex-col md:flex-row justify-between items-start md:items-center mb-8">
          <h1 className="text-3xl font-bold text-primary mb-4 md:mb-0">
            {filters.categories.length === 1
              ? filters.categories[0].charAt(0).toUpperCase() +
                filters.categories[0].slice(1)
              : "All Products"}
          </h1>

          <div className="flex flex-wrap items-center gap-4">
            {/* View Toggle */}
            <div className="flex items-center bg-gray-100 rounded-md p-1">
              <button
                onClick={() => setGridView(true)}
                className={`p-2 rounded-md ${
                  gridView
                    ? "bg-white text-primary shadow-sm"
                    : "text-gray-500 hover:text-primary"
                }`}
                aria-label="Grid view"
              >
                <FaThLarge />
              </button>
              <button
                onClick={() => setGridView(false)}
                className={`p-2 rounded-md ${
                  !gridView
                    ? "bg-white text-primary shadow-sm"
                    : "text-gray-500 hover:text-primary"
                }`}
                aria-label="List view"
              >
                <FaThList />
              </button>
            </div>

            {/* Sort Dropdown */}
            <div className="flex items-center">
              <FaSort className="text-gray-500 mr-2" />
              <select
                value={sortBy}
                onChange={handleSortChange}
                className="bg-gray-100 border-none rounded-md py-2 px-3 focus:outline-none focus:ring-2 focus:ring-accent text-gray-700"
              >
                <option value="newest">Newest</option>
                <option value="price-low-high">Price: Low to High</option>
                <option value="price-high-low">Price: High to Low</option>
                <option value="rating">Rating</option>
                <option value="popularity">Popularity</option>
              </select>
            </div>

            {/* Mobile Filters Button */}
            <div className="md:hidden">
              <FilterSidebar
                filters={filters}
                setFilters={setFilters}
                isMobile={true}
                mobileOpen={mobileFiltersOpen}
                setMobileOpen={setMobileFiltersOpen}
                resetFilters={resetFilters}
              />
            </div>
          </div>
        </div>

        <div className="flex flex-col md:flex-row gap-8">
          {/* Sidebar - Desktop */}
          <div className="hidden md:block w-full md:w-64 lg:w-72">
            <FilterSidebar
              filters={filters}
              setFilters={setFilters}
              resetFilters={resetFilters}
            />
          </div>

          {/* Products Grid */}
          <div className="flex-1">
            {displayedProducts.length === 0 ? (
              <div className="text-center py-16 text-gray-500">
                <FaFilter className="w-12 h-12 mx-auto text-gray-300 mb-4" />
                <h3 className="text-xl font-medium text-gray-700 mb-2">
                  No products found
                </h3>
                <p className="mb-6">
                  Try adjusting your filters or search criteria
                </p>
                <button
                  onClick={resetFilters}
                  className="bg-primary text-white px-4 py-2 rounded-md hover:bg-accent transition-colors"
                >
                  Reset Filters
                </button>
              </div>
            ) : (
              <>
                <p className="text-gray-500 mb-4">
                  Showing {displayedProducts.length} products
                </p>

                <div
                  className={
                    gridView
                      ? "grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6"
                      : "space-y-6"
                  }
                >
                  {displayedProducts.map((product) => (
                    <motion.div
                      key={product.id}
                      initial={{ opacity: 0 }}
                      animate={{ opacity: 1 }}
                      transition={{ duration: 0.3 }}
                    >
                      <ProductCard product={product} />
                    </motion.div>
                  ))}
                </div>

                {/* Pagination Placeholder - In a real app, implement proper pagination */}
                <div className="flex justify-center mt-10">
                  <button className="w-10 h-10 flex items-center justify-center rounded-md bg-primary text-white">
                    1
                  </button>
                  <button className="w-10 h-10 flex items-center justify-center rounded-md text-primary hover:bg-gray-100 ml-2">
                    2
                  </button>
                  <button className="w-10 h-10 flex items-center justify-center rounded-md text-primary hover:bg-gray-100 ml-2">
                    3
                  </button>
                  <span className="w-10 h-10 flex items-center justify-center">
                    ...
                  </span>
                  <button className="w-10 h-10 flex items-center justify-center rounded-md text-primary hover:bg-gray-100">
                    10
                  </button>
                </div>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Shop;
