import React, { useState, useEffect } from "react";
import { useLocation, useNavigate, useParams } from "react-router-dom";
import { motion } from "framer-motion";
import { FaFilter, FaSort } from "react-icons/fa";

import Breadcrumb from "../components/Layout/Breadcrumb";
import ProductCard from "../components/Common/ProductCard";
import FilterSidebar from "../components/Common/FilterSidebar";
import products from "../data/products";
import categories from "../data/categories";

const Shop = () => {
  const location = useLocation();
  const navigate = useNavigate();
  const { category } = useParams();
  const queryParams = new URLSearchParams(location.search);
  const searchQuery = queryParams.get("search");
  
  // State for filters and sorting
  const [filters, setFilters] = useState({
    categories: category ? [category] : [],
    subcategories: [],
    price: {
      min: "",
      max: "",
    },
    sizes: [],
    colors: [],
  });

  const [sortBy, setSortBy] = useState("featured");
  const [mobileFiltersOpen, setMobileFiltersOpen] = useState(false);
  const [displayProducts, setDisplayProducts] = useState([]);
  const [isLoading, setIsLoading] = useState(true);

  // Update filters when URL params change
  useEffect(() => {
    if (category) {
      setFilters((prev) => ({
        ...prev,
        categories: [category],
      }));
    }
    
    // Get subcategory from URL if present
    const subcategory = queryParams.get("subcategory");
    if (subcategory && category) {
      setFilters((prev) => ({
        ...prev,
        subcategories: [`${category}_${subcategory}`],
      }));
    }
    
    // Simulate loading
    setIsLoading(true);
    setTimeout(() => {
      setIsLoading(false);
    }, 500);
  }, [category, queryParams]);

  // Filter and sort products
  useEffect(() => {
    let filtered = [...products];
    
    // Filter by search query if present
    if (searchQuery) {
      const query = searchQuery.toLowerCase();
      filtered = filtered.filter(product => 
        product.name.toLowerCase().includes(query) || 
        product.description.toLowerCase().includes(query) || 
        product.category.toLowerCase().includes(query) || 
        (product.tags && product.tags.some(tag => tag.toLowerCase().includes(query)))
      );
    }
    // Filter by category
    else if (filters.categories.length > 0) {
      filtered = filtered.filter((product) =>
        filters.categories.includes(product.category)
      );
    }

    // Filter by subcategory
    if (filters.subcategories.length > 0) {
      filtered = filtered.filter((product) =>
        filters.subcategories.some((sub) => {
          const [cat, subcat] = sub.split("_");
          return product.category === cat && product.subCategory === subcat;
        })
      );
    }

    // Filter by price
    if (filters.price.min !== "") {
      filtered = filtered.filter(
        (product) => product.price >= parseFloat(filters.price.min)
      );
    }
    if (filters.price.max !== "") {
      filtered = filtered.filter(
        (product) => product.price <= parseFloat(filters.price.max)
      );
    }

    // Filter by size
    if (filters.sizes.length > 0) {
      filtered = filtered.filter((product) =>
        product.sizes.some((size) => filters.sizes.includes(size))
      );
    }

    // Filter by color
    if (filters.colors.length > 0) {
      filtered = filtered.filter((product) =>
        product.colors.some((color) => filters.colors.includes(color))
      );
    }

    // Sort products
    switch (sortBy) {
      case "price-low-high":
        filtered.sort((a, b) => a.price - b.price);
        break;
      case "price-high-low":
        filtered.sort((a, b) => b.price - a.price);
        break;
      case "newest":
        filtered.sort((a, b) => b.id - a.id);
        break;
      case "rating":
        filtered.sort((a, b) => b.rating - a.rating);
        break;
      case "featured":
      default:
        filtered.sort((a, b) => (a.featured === b.featured ? 0 : a.featured ? -1 : 1));
        break;
    }

    setDisplayProducts(filtered);
  }, [filters, sortBy, searchQuery]);

  // Get current category details
  const currentCategory = category
    ? categories.find((cat) => cat.id === category)
    : null;

  // Get breadcrumb items
  const breadcrumbItems = [
    { label: "Home", path: "/" },
    { label: "Shop", path: "/shop" },
  ];

  if (currentCategory) {
    breadcrumbItems.push({
      label: currentCategory.name,
      path: `/shop/${currentCategory.id}`,
    });
  }
  
  if (searchQuery) {
    breadcrumbItems.push({
      label: `Search: ${searchQuery}`,
      path: `/shop?search=${searchQuery}`,
    });
  }

  // Reset filters
  const handleResetFilters = () => {
    setFilters({
      categories: category ? [category] : [],
      subcategories: [],
      price: {
        min: "",
        max: "",
      },
      sizes: [],
      colors: [],
    });
  };

  // Apply filters (for mobile)
  const handleApplyFilters = () => {
    // Close mobile filters
    setMobileFiltersOpen(false);
  };

  return (
    <div className="pt-20">
      <Breadcrumb items={breadcrumbItems} />

      <div className="container mx-auto px-4 py-8">
        {/* Page Title */}
        <div className="mb-8">
          {searchQuery ? (
            <h1 className="text-3xl font-bold text-primary">
              Search Results: "{searchQuery}"
            </h1>
          ) : currentCategory ? (
            <h1 className="text-3xl font-bold text-primary">
              {currentCategory.name}
            </h1>
          ) : (
            <h1 className="text-3xl font-bold text-primary">All Products</h1>
          )}
          {currentCategory && (
            <p className="text-gray-600 mt-2">{currentCategory.description}</p>
          )}
        </div>

        <div className="flex flex-col md:flex-row gap-8">
          {/* Sidebar */}
          <div className="md:w-1/4">
            <div className="hidden md:block">
              <FilterSidebar
                filters={filters}
                setFilters={setFilters}
                resetFilters={handleResetFilters}
              />
            </div>
            <div className="md:hidden">
              <FilterSidebar
                filters={filters}
                setFilters={setFilters}
                isMobile={true}
                mobileOpen={mobileFiltersOpen}
                setMobileOpen={setMobileFiltersOpen}
                resetFilters={handleResetFilters}
                applyFilters={handleApplyFilters}
              />
            </div>
          </div>

          {/* Product Grid */}
          <div className="md:w-3/4">
            {/* Toolbar */}
            <div className="flex flex-wrap justify-between items-center mb-6 gap-4">
              <div className="flex items-center">
                <span className="text-gray-600 mr-2">
                  {displayProducts.length} Products
                </span>
              </div>

              <div className="flex items-center gap-4">
                {/* Mobile Filter Button */}
                <div className="md:hidden">
                  <button
                    onClick={() => setMobileFiltersOpen(true)}
                    className="flex items-center justify-center bg-primary text-white px-4 py-2 rounded-md"
                  >
                    <FaFilter className="mr-2" /> Filters
                  </button>
                </div>

                {/* Sort Dropdown */}
                <div className="relative">
                  <select
                    value={sortBy}
                    onChange={(e) => setSortBy(e.target.value)}
                    className="appearance-none bg-white border border-gray-300 rounded-md py-2 pl-4 pr-10 focus:outline-none focus:ring-1 focus:ring-accent focus:border-accent"
                  >
                    <option value="featured">Featured</option>
                    <option value="price-low-high">Price: Low to High</option>
                    <option value="price-high-low">Price: High to Low</option>
                    <option value="newest">Newest</option>
                    <option value="rating">Top Rated</option>
                  </select>
                  <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-2 text-gray-700">
                    <FaSort className="w-4 h-4" />
                  </div>
                </div>
              </div>
            </div>

            {/* Products */}
            {isLoading ? (
              // Loading skeleton
              <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
                {[...Array(6)].map((_, index) => (
                  <div
                    key={index}
                    className="bg-gray-100 rounded-lg aspect-[3/4] animate-pulse"
                  ></div>
                ))}
              </div>
            ) : displayProducts.length > 0 ? (
              <motion.div
                initial={{ opacity: 0 }}
                animate={{ opacity: 1 }}
                transition={{ duration: 0.5 }}
                className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6"
              >
                {displayProducts.map((product) => (
                  <ProductCard key={product.id} product={product} />
                ))}
              </motion.div>
            ) : (
              <div className="text-center py-12">
                <h3 className="text-xl font-medium text-gray-600 mb-4">
                  No products found
                </h3>
                <p className="text-gray-500 mb-6">
                  Try adjusting your filters or search criteria.
                </p>
                <button
                  onClick={handleResetFilters}
                  className="bg-primary text-white px-6 py-2 rounded-md hover:bg-accent transition-colors"
                >
                  Reset Filters
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

